export function TechStackSection() {
  const t = useTranslations("techStack")
  const locale = useLocale() as Locale
  const isRTL = isRtlLocale(locale)

  // Duplicate technologies for seamless infinite scroll
  const duplicatedTechs = [...technologies, ...technologies]

  return (
    <section className="bg-muted/30 py-16 overflow-hidden">
      <div className="container mx-auto px-4 md:px-6 mb-10">
        <div className="mx-auto max-w-3xl text-center">
          <div className="mb-4 inline-block border border-accent/30 bg-accent/10 px-4 py-1.5 text-xs font-medium uppercase tracking-wider text-accent">
            {t("badge")}
          </div>
          <h2 className={`text-3xl md:text-4xl lg:text-5xl font-bold tracking-tight mb-4 ${isRTL ? "leading-relaxed" : ""}`}>
            {t("title")}
          </h2>
          <p className={`text-muted-foreground text-base md:text-lg max-w-2xl mx-auto ${isRTL ? "leading-loose" : ""}`}>
            {t("description")}
          </p>
        </div>
      </div>

      {/* Infinite Scroll Banner */}
      <div className="container mx-auto px-4 md:px-6">
        <div className="relative">
          {/* Left fade */}
          <div className="absolute left-0 top-0 bottom-0 w-24 md:w-36 z-20 pointer-events-none">
            <div className="absolute inset-0 bg-gradient-to-r from-background to-transparent" />
            <div className="absolute inset-0 bg-gradient-to-r from-muted/30 via-muted/20 to-transparent" />
          </div>

          {/* Right fade */}
          <div className="absolute right-0 top-0 bottom-0 w-24 md:w-36 z-20 pointer-events-none">
            <div className="absolute inset-0 bg-gradient-to-l from-background to-transparent" />
            <div className="absolute inset-0 bg-gradient-to-l from-muted/30 via-muted/20 to-transparent" />
          </div>

          {/* Scrolling container - force LTR for consistent animation */}
          <div className="flex overflow-hidden rounded-xl" dir="ltr">
            <div
              className="flex gap-6 py-4 animate-scroll"
              style={{ width: "fit-content" }}
            >
              {duplicatedTechs.map((tech, index) => (
                <div
                  key={`${tech.name}-${index}`}
                  className="flex flex-col items-center gap-3 px-6 py-4 rounded-xl bg-background border border-border/50 shadow-sm min-w-[120px]"
                >
                  <div className="text-foreground/80">
                    {tech.icon}
                  </div>
                  <span className="text-sm font-medium text-muted-foreground whitespace-nowrap">
                    {tech.name}
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  )
}
